value.value:
@string(
    div(
        div(
            div(
                div(
                    sub(
                        ticks(
                            formatDateTime(utcNow(), 'yyyy/MM/dd HH:mm:ss')
                        ),
                        ticks(
                            if(
                                empty(pipeline().parameters.p_data_inicio_carga), 
                                formatDateTime(addDays(utcNow(),-90), 'yyyy/MM/dd HH:mm:ss'),
                                formatDateTime(pipeline().parameters.p_data_inicio_carga, 'yyyy/MM/dd HH:mm:ss') 
                            )   
                        )
                    ),
                    10000000
                ),
                60
            ),
            60
        ),
        24
    )
)
