notebookPath.value:
@concat(
    if(
        endswith(
            trim(pipeline().parameters.p_caminho_raiz_notebook),
            '/'
        ),
        trim(pipeline().parameters.p_caminho_raiz_notebook),
        concat(trim(pipeline().parameters.p_caminho_raiz_notebook),'/')
    ),
    if(
        startswith(
            trim(pipeline().parameters.p_caminho_notebook),
            '/'
        ),
        substring(
            trim(pipeline().parameters.p_caminho_notebook),
            1,
            sub(length(pipeline().parameters.p_caminho_notebook),1)
        ),
        trim(pipeline().parameters.p_caminho_notebook)
    ),
    if(
        endswith(
            trim(pipeline().parameters.p_caminho_notebook), 
            '/'
        ),
        if(
            startswith(
                trim(pipeline().parameters.p_nome_notebook), 
                '/'
            ),
            substring(
                trim(pipeline().parameters.p_nome_notebook),
                1,
                sub(length(pipeline().parameters.p_nome_notebook),1)
            ),
            trim(pipeline().parameters.p_nome_notebook)
        ), 
        if(
            startswith(
                trim(pipeline().parameters.p_nome_notebook), 
                '/'
            ),
            trim(pipeline().parameters.p_nome_notebook),
            concat('/', trim(pipeline().parameters.p_nome_notebook))
        )
    )
)
